INSERT INTO `dnfdungeon` (`id`, `name`, `imgsrc`, `position`, `boss`, `intro`) VALUES
(1, '炽晶森林', 'http://127.0.0.1:8081/public/img/selectdungeonslot/abnoba/0.png', '亚诺法森林', '火焰公主婕拉', '精灵们的安身之所，但在毁灭纪中已经变得支离破碎。虽然借助银色村庄的力量，森林的资源和生态得到了恢复，但是曾经失去家园的精灵们，现在对外人的警惕心变得越来越强，也越来越具有攻击性，冒险家们，请一定要小心……'),
(2, '哥布林王国', 'http://127.0.0.1:8081/public/img/selectdungeonslot/demension/0.png', '异次元裂缝', '哥布林王高格', '在xx年xx月xx日的更新中，进行了副本难度上的调整，将哥布林王国归入了融合的异次元裂缝这一副本之中。');

